INSERT INTO `food-app`.`restaurant` (`id`, `description`, `image`, `name`) VALUES 
('1', 'Kentucky Fried Chicken este unul dintre cele mai mari lanțuri de restaurante de tipul fast-food din lume, vânzând cu prioritate preparate din pui, cartofi prăjiți, hamburgeri, și cheeseburgeri, cu sediul în Louisville, Kentucky. Deține peste 15.000 de restaurante în 109 țări, din care 5.200 sunt în Statele Unite ale Americii.', '\images\KFC_logo.png', 'KFC'),
('2', 'McDonald''s Corporation este un lanț de restaurante de tipul fast-food din lume, vânzând cu prioritate hamburgeri, pui, cartofi prăjiți, amestecuri de băuturi răcoritoare pe bază de lapte (așa numitele milkshake-uri) și băuturi carbo-gazoase. Mai recent, a început să ofere și salate, fructe, snack wraps și carrot sticks.', '\images\mcDonalds.png', 'McDonald''s'),
('3', 'Mama Manu este unul dintre cele mai cunoscute și iubite restaurante din orașul Cluj-Napoca. Ce ne diferențiază de restul?  În primul rand este una dintre puținele locații din oraș deschise non–stop, mâncarea este gătită după o rețetă unică în țară, iar dragostea pentru gustul delicios se simte în fiecare farfurie.', '\images\MamaManu.jpg', 'Mama Manu'),
('4', 'Specialitățile Spartan sunt inspirate din bucătăria grecească însă modul de preparare este unul propriu, iar gama largă de ingrediente duce la o combinație unică de gusturi.', '\images\Spartan.png', 'Spartan');